import React from "react";
import "./FormComponents.css";

export const Input = ({
  type,
  id,
  value,
  required,
  additionalClass,
  name,
  placeholder,
  manipulationFunction,
}) => {
  return (
    <input
      type={type}
      id={id}
      name={name}
      value={value}
      required={required}
      className={`input-component ${additionalClass}`}
      placeholder={placeholder}
      onChange={manipulationFunction}
      autoComplete="off"
    />
  );
};

export const Button = ({
  textButton,
  id,
  name,
  type,
  additionalClass = "",
  manipulationFunction,
}) => {
  return (
    <button
      type={type}
      name={name}
      id={id}
      className={`button-component ${additionalClass}`}
      onClick={manipulationFunction}
    >
      {textButton}
    </button>
  );
};

const dados = [
  { value: "1234", text: "Vestu치rio Masculino" },
  { value: "4321", text: "Vestu치rio Feminino" },
  { value: "a1fdc28b-496f-4d99-808c-c7095649bf56", text: "Java Script" },
];

export const Select = ({ 
    dados = [],
    id,
    name,
    require,
    additionalClass = "",
    manipulationFunction,
    defaultValue,

}) => {
  return (
    <select 
        id={id}
        name={name}
        required={require}
        className={`input-component ${additionalClass}`}
        onChange={manipulationFunction}
        value={defaultValue}
    >

      <option value="">Selecione</option>
      {dados.map((e) => {
        return (
          <option key={e.value} value={e.value}>
            {e.text}
          </option>
        );
      })}
    </select>
  );
};
